/**
* Front-End News! Stylesheet
* 
* Indigo         : #3f51b5;
* Indigo (dark)  : #303f9f;
* Light Blue     : #c5cae9;
* White          : #ffffff;
* Pink           : #ff4081;
* Gray (dark)    : #212121;
* Gray (medium)  : #727272;
* Gray (light)   : #b6b6b6;
*/

:root {
  --indigo: #3f51b5;
  --indigo_dark: #303f9f;
  --light Blue: #c5cae9;
  --white: #ffffff;
  --pink: #ff4081;
  --gray_dark: #212121;
  --gray_medium: #727272;
  --gray_light: #b6b6b6; 
}

/* =====================
* BASE ================= */

body {

}

h1, h2, h3, h4, h5, h6 {

}

ol {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

li {

}

p {

}

a {

}

/* =====================
* HEADER =============== */

.hero {
  background-color: var(--indigo);
  padding: 20px 0;
  margin-bottom: 40px;
  color: var(--white);
  text-align: center;
}

/* =====================
* LOGO =============== */

.logo {
  font-size: 34px;
  text-transform: uppercase;
  font-weight: bold;
  font-family: Arial, serif;
}

/* =====================
* NEWS =============== */

.news-item {
  border-bottom: 1px solid var(--gray_light);
  margin-bottom: 20px;
  padding-bottom: 20px;
}

.news-link {
  color: var(--indigo);
  font-family: Arial, serif;
  font-size: 24px;
}

/* =====================
* CONTAINER =============== */

.container {
  max-width: 800px;
  margin: 0 auto;
}
