/**
* Front-End News! Stylesheet
* 
* Indigo         : #3f51b5;
* Indigo (dark)  : #303f9f;
* Light Blue     : #c5cae9;
* White          : #ffffff;
* Pink           : #ff4081;
* Gray (dark)    : #212121;
* Gray (medium)  : #727272;
* Gray (light)   : #b6b6b6;
*/

:root {
  --indigo: #3f51b5;
  --indigo_dark: #303f9f;
  --light_blue: #c5cae9;
  --white: #ffffff;
  --pink: #ff4081;
  --gray_dark: #212121;
  --gray_medium: #727272;
  --gray_light: #b6b6b6; 
}

/* =====================
* BASE ================= */

body {
  color: var(--gray_dark);
  font-family: 'Lato', Arial, sans-serif;
  font-size: 16px;
  line-height: 1.5;
}

h1, h2, h3, h4, h5, h6 {
  margin-bottom: 5px;
  margin-top: 0;
}

ol, ul {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

li {

}

p {

}

a {
  color: var(--pink);
  transition: .2s ease-in-out;
}

a:active,
a:hover,
a:focus {
  color: var(--gray_dark);
}

/* =====================
* HEADER =============== */

.hero {
  background-color: var(--indigo);
  padding: 20px 0;
  margin-bottom: 40px;
  color: var(--white);
  text-align: center;
}

/* =====================
* LOGO ================= */

.logo {
  font-size: 34px;
  text-transform: uppercase;
  font-weight: bold;
  font-family: Arial, serif;
}

/* =====================
* NEWS ================= */

.news-item {
  border-bottom: 1px solid var(--gray_light);
  margin-bottom: 20px;
  padding-bottom: 20px;
}

.news-item:last-child {
  border-bottom: 0;
  margin-bottom: 0;
  padding-bottom: 0;
}

.news-link {
  color: var(--indigo);
  display: block;
  font-family: Arial, serif;
  font-size: 24px;
  margin-bottom: 5px;
}

.news-meta li {
  display: inline;
  margin-right: 15px;
}

/* =====================
* CONTAINER ============ */

.container {
  max-width: 800px;
  margin: 0 auto;
}

.content {
  margin-bottom: 20px; 
}

/* =====================
* MODAL ================ */

.modal {
  top: 20px;
  position: absolute;
  left: 50%;
  opacity: 0;
  visibility: hidden;
  transform: translate(-50%, 0);
  transition: .2s ease-in-out;
  background-color: var(--white);
  width: 500px;
  border-bottom-right-radius: 4px; 
  border-bottom-left-radius: 4px; 
  box-shadow: 0 2px 6px rgba(0, 0, 0, .4);
  z-index: 2;
}

.modal.is-visible {
  opacity: 1;
  visibility: visible;
}

.modal-media {
  position: relative;
}

.modal-media-title {
  color: var(--white);
  font-size: 1.9rem;
  bottom: 20px;
  left: 20px;
  position: absolute;
}

.modal-overlay {
  background-color: var(--gray_dark);
  position: fixed;
  left: 0;
  bottom: 0;
  opacity: .3;
  right: 0;
  top: 0;
  transition: .2s ease-in-out;
  width: 100%;
  height: 100%;
  z-index: 1;
  opacity: 0;
  visibility: hidden;
}

.modal-overlay.is-visible {
  opacity: .3;
  visibility: visible;
}

.modal-content {
  padding: 20px;
}

/* =====================
* ANIMATION ============ */

@keyframes flash {
  from, 50%, to {
    opacity: 0;
  }

  25%, 75% {
    opacity: 1;
  }
}

/* =====================
* FORM ================= */

.form {
  margin-bottom: 20px;
  opacity: 0;
  visibility: hidden;
  height: 0;
  transition: opacity .2s ease-in-out, visibility .2s ease-in-out;
}

/* =====================
* STATES =============== */

.is-liked {
  animation: flash 2s;
}

.is-visible {
  opacity: 1;
  visibility: visible;
  height: auto;
}

.form-label {
  display: block; 
  font-weight: bold;
  margin-bottom: 5px;
}

.form-input {
  border: 1px solid var(--gray_light);
  border-radius: 4px;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  width: 100%;
  padding: 10px;
  font-size: 20px;
}
