/**
* Front-End News! Stylesheet
* 
* Indigo         : #3f51b5;
* Indigo (dark)  : #303f9f;
* Light Blue     : #c5cae9;
* White          : #ffffff;
* Pink           : #ff4081;
* Gray (dark)    : #212121;
* Gray (medium)  : #727272;
* Gray (light)   : #b6b6b6;
*/

:root {
  --indigo: #3f51b5;
  --indigo_dark: #303f9f;
  --light_blue: #c5cae9;
  --white: #ffffff;
  --pink: #ff4081;
  --gray_dark: #212121;
  --gray_medium: #727272;
  --gray_light: #b6b6b6; 
}

/* =====================
* BASE ================= */

body {
  color: var(--gray_dark);
  font-family: 'Lato', Arial, sans-serif;
  font-size: 16px;
  line-height: 1.5;
}

h1, h2, h3, h4, h5, h6 {

}

ol, ul {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

li {

}

p {

}

a {
  color: var(--pink);
}

a:active,
a:hover,
a:focus {
  color: var(--gray_dark);
}

/* =====================
* HEADER =============== */

.hero {
  background-color: var(--indigo);
  padding: 20px 0;
  margin-bottom: 40px;
  color: var(--white);
  text-align: center;
}

/* =====================
* LOGO ================= */

.logo {
  font-size: 34px;
  text-transform: uppercase;
  font-weight: bold;
  font-family: Arial, serif;
}

/* =====================
* NEWS ================= */

.news-item {
  border-bottom: 1px solid var(--gray_light);
  margin-bottom: 20px;
  padding-bottom: 20px;
}

.news-link {
  color: var(--indigo);
  display: block;
  font-family: Arial, serif;
  font-size: 24px;
  margin-bottom: 5px;
}

.news-meta li {
  display: inline;
  margin-right: 15px;
}

/* =====================
* CONTAINER ============ */

.container {
  max-width: 800px;
  margin: 0 auto;
}

/* =====================
* STATES =============== */

.is-liked {
  animation: flash 2s;
}

/* =====================
* ANIMATION ============ */

@keyframes flash {
  from, 50%, to {
    opacity: 0;
  }

  25%, 75% {
    opacity: 1;
  }
}
